import GridCard from './GridCard';

function GridCardFactory() {
    var rows = [];
    var cards = [];
    var noCards = 7;

    for(var i = 0; i < noCards; i++) {
        if (i == 3) {
            rows.push(
                <div className="row" class="d-flex justify-content-center">
                    {cards}
                </div>
            );
            cards = [];
        }
        cards.push(<GridCard />);
    }



    return (
        <div className="container-fluid">
            {rows}
        </div>
    )
}

export default GridCardFactory;
