function cardsToRows(cards) {
    return (
    <div className="row" class="d-flex justify-content-center">
        {cards}
    </div>)
}

const GridCardFactory = ({movies}) => {
    var rows = [];
    var currentRow = [];
    var cards = movies.map((movie) => {
        cards.push(
        <GridCard imageUrl={movie.Poster} title={movie.Title} year={movie.Year} />
        )
    }
    );

    console.log("Made it");

    console.log("1");
    console.log(cards);

    for(var i = 0; i < cards.length; i++) {
        console.log("1.1");
        if (i%3==0) {
            console.log("1.2");
            rows.push(cardsToRows(currentRow));
            console.log("1.3");
            currentRow = [];
        }
        console.log("1.4");
        currentRow.push(cards[i]);
        console.log("1.5");
    }
    console.log("2");

    rows.push(cardsToRows(currentRow));

    console.log("3");
    return (
        <div className="container-fluid">
            {rows}
        </div>
    )
}

export default GridCardFactory;
