import {useState} from "react"

const SearchBar = ({onSearch}) => {
    const [searchQuery, setSearchQuery] = useState('');

    const handleSubmit = (event) => {
        event.preventDefault();
        console.log("Searching for ", searchQuery);
        onSearch(searchQuery);
    }

    const handleOnChange = (event) => {
        setSearchQuery(event.target.value);
    }

    return (
        <form onSubmit={handleSubmit} class="input-group-text">
            <input type="text" class="input-group-text" autofocus="autofocus" value={searchQuery} onChange={handleOnChange} style={{ width: '18rem', margin: '16px'}}/>
            <button type="submit" class="btn btn-primary" style={{ width: '18rem', margin: '16px'}}>Search</button>
        </form>
    )
}

export default SearchBar
